mapper.select:
select s.id seatId, s.qunar_name seatName, bsm.busi_id bType, bsm.busi_supplier_id bSuId, bsm.bsuid_and_type bSuIdAndTypeId, bsm.supplier_id as supplierId, supplier.name as supplierName, supplier.logo_url as supplierLogo from busi_supplier_mapping bsm, seat s, supplier where bsm.supplier_id = s.supplier_id and supplier.id = s.supplier_id and s.host = 'ejabhost2' and s.status = 1
and bsm.bsuid_and_type in
#{bSuIdAndType}

select string_agg(qunar_name,',') from seat s,busi_supplier_mapping bbm where bbm.busi_id = #{busiType} and s.supplier_id = bbm.supplier_id AND s.host = 'ejabhost2' and s.status = 1
and bbm.busi_supplier_id in
#{supplierId}

select string_agg(qunar_name,',') from seat s where s.host = 'ejabhost2'AND s.status = 1
and supplier_id in
#{supplierId}

select string_agg(qunar_name,',') from seat s where s.host = 'ejabhost2'
AND supplier_id in
#{supplierId}

SELECT
, sg.id AS groupId,sg.name AS groupName ,sg.strategy AS strategy ,service_status as serviceState FROM seat, seat_group_mapping sgm, seat_group sg, busi_seat_mapping bsm WHERE seat.status = 1
AND sg.id IN
#{groupId}
AND sg.id = sgm.group_id AND sgm.seat_id = seat.id AND bsm.seat_id = seat.id AND seat.supplier_id = sg.supplier_id AND seat.host = 'ejabhost2' and seat.status = 1
AND bsm.busi_id = #{busiId}
